+import { formatDistanceToNow, isToday, isYesterday } from 'date-fns';
+import type { Notification } from '@/types';
+
+type FilterType = 'all' | 'unread' | 'read';
+
+const notificationIconMap: Record<
+  Notification['type'],
+  { icon: typeof Calendar; color: string; bg: string }
+> = {
+  'meeting-starting': {
+    icon: Calendar,
+    color: 'text-blue-600 dark:text-blue-400',
+    bg: 'bg-blue-50 dark:bg-blue-950/30',
+  },
+  'note-ready': {
+    icon: FileText,
+    color: 'text-emerald-600 dark:text-emerald-400',
+    bg: 'bg-emerald-50 dark:bg-emerald-950/30',
+  },
+  'share-received': {
+    icon: Share2,
+    color: 'text-purple-600 dark:text-purple-400',
+    bg: 'bg-purple-50 dark:bg-purple-950/30',
+  },
+  'team-invite': {
+    icon: UserPlus,
+    color: 'text-orange-600 dark:text-orange-400',
+    bg: 'bg-orange-50 dark:bg-orange-950/30',
+  },
+  system: {
+    icon: Info,
+    color: 'text-[var(--color-text-tertiary)]',
+    bg: 'bg-[var(--color-bg-tertiary)]',
+  },
+};
+
+function groupNotifications(notifications: Notification[]) {
+  const groups: { label: string; items: Notification[] }[] = [];
+  const todayItems: Notification[] = [];
+  const yesterdayItems: Notification[] = [];
+  const earlierItems: Notification[] = [];
+
+  for (const n of notifications) {
+    const ts = new Date(n.timestamp);
+    if (isToday(ts)) {
+      todayItems.push(n);
+    } else if (isYesterday(ts)) {
+      yesterdayItems.push(n);
+    } else {
+      earlierItems.push(n);
+    }
+  }
+
+  if (todayItems.length > 0) groups.push({ label: 'Today', items: todayItems });
+  if (yesterdayItems.length > 0)
+    groups.push({ label: 'Yesterday', items: yesterdayItems });
+  if (earlierItems.length > 0)
+    groups.push({ label: 'Earlier', items: earlierItems });
+
+  return groups;
+}
+
+export default function NotificationsScreen() {
+  const navigate = useNavigate();
+  const { notifications, unreadCount, markAsRead, markAllAsRead } =
+    useNotificationsStore();
+  const [filter, setFilter] = useState<FilterType>('all');
+  const [dismissedIds, setDismissedIds] = useState<Set<string>>(new Set());
+
+  const filteredNotifications = useMemo(() => {
+    let filtered = notifications.filter((n) => !dismissedIds.has(n.id));
+    if (filter === 'unread') filtered = filtered.filter((n) => !n.read);
+    if (filter === 'read') filtered = filtered.filter((n) => n.read);
+    return filtered;
+  }, [notifications, filter, dismissedIds]);
+
+  const groups = useMemo(
+    () => groupNotifications(filteredNotifications),
+    [filteredNotifications]
+  );
+
+  const handleNotificationClick = (notification: Notification) => {
+    if (!notification.read) {
+      markAsRead(notification.id);
+    }
+    if (notification.actionUrl) {
+      navigate(notification.actionUrl);
+    }
+  };
+
