+    { delay: 0.15, duration: 0.35 },
+    { delay: 0.1, duration: 0.45 },
+    { delay: 0.2, duration: 0.38 },
+  ];
+
+  return (
+    <div className="flex items-end gap-0.5 h-3">
+      {bars.map((bar, i) => (
+        <motion.div
+          key={i}
+          className="w-0.5 bg-[var(--color-recording)] rounded-full"
+          animate={{ height: ['3px', '12px', '5px', '10px', '3px'] }}
+          transition={{
+            duration: bar.duration + 0.3,
+            repeat: Infinity,
+            delay: bar.delay,
+            ease: 'easeInOut',
+          }}
+        />
+      ))}
+    </div>
+  );
+}
