+import { useState } from 'react';
+import { useLocation } from 'react-router-dom';
+import { motion, AnimatePresence } from 'framer-motion';
+import { cn } from '@/lib/cn';
+import SidebarItem from './SidebarItem';
+import {
+  Home,
+  FileText,
+  Search,
+  LayoutTemplate,
+  Sparkles,
+  MessageSquare,
+  Settings,
+  FolderOpen,
+  ChevronLeft,
+  ChevronRight,
+  Plus,
+  Users,
+  ChevronDown,
+} from 'lucide-react';
+
+interface SidebarProps {
+  collapsed: boolean;
+  onToggleCollapse: () => void;
+}
+
+const primaryNav = [
+  { icon: Home, label: 'Home', to: '/', end: true },
+  { icon: FileText, label: 'Notes', to: '/notes' },
+  { icon: Search, label: 'Search', to: '/search' },
+  { icon: LayoutTemplate, label: 'Templates', to: '/templates' },
+  { icon: Sparkles, label: 'Recipes', to: '/recipes' },
+  { icon: MessageSquare, label: 'AI Chat', to: '/ai-chat' },
+];
+
+const folders = [
+  { id: '1', name: 'Product Meetings', count: 12, color: '#6366f1' },
+  { id: '2', name: 'Engineering', count: 8, color: '#10b981' },
+  { id: '3', name: 'Customer Calls', count: 24, color: '#f59e0b' },
+  { id: '4', name: 'All Hands', count: 5, color: '#ef4444' },
+];
+
+export default function Sidebar({ collapsed, onToggleCollapse }: SidebarProps) {
+  const [foldersOpen, setFoldersOpen] = useState(true);
+  const location = useLocation();
+
+  return (
+    <motion.aside
+      className={cn(
+        'flex h-full flex-col border-r border-[var(--color-border-primary)] bg-[var(--color-bg-sidebar)]',
+      )}
+      initial={false}
+      animate={{ width: collapsed ? 64 : 240 }}
+      transition={{ duration: 0.2, ease: [0.25, 0.1, 0.25, 1] }}
+    >
+      {/* Logo area */}
+      <div className="flex h-12 shrink-0 items-center border-b border-[var(--color-border-primary)] px-4">
+        <div className="flex items-center gap-2.5 overflow-hidden">
+          <div className="flex h-7 w-7 shrink-0 items-center justify-center rounded-[var(--radius-md)] bg-[var(--color-accent)] text-white">
+            <Sparkles className="h-4 w-4" />
+          </div>
+          <AnimatePresence>
+            {!collapsed && (
+              <motion.span
+                initial={{ opacity: 0, width: 0 }}
+                animate={{ opacity: 1, width: 'auto' }}
+                exit={{ opacity: 0, width: 0 }}
+                transition={{ duration: 0.15 }}
+                className="truncate text-lg font-semibold text-[var(--color-text-primary)]"
+              >
+                Granola
+              </motion.span>
+            )}
+          </AnimatePresence>
+        </div>
+      </div>
+
+      {/* Primary navigation */}
+      <nav className="flex flex-col gap-0.5 px-2 pt-3">
+        {primaryNav.map((item) => (
+          <SidebarItem
+            key={item.to}
+            icon={item.icon}
+            label={item.label}
+            to={item.to}
+            collapsed={collapsed}
+            end={item.end}
+          />
+        ))}
+      </nav>
+
+      {/* Workspace section */}
+      <div className="mt-4 px-2">
+        {!collapsed && (
+          <div className="mb-1 px-3 text-[11px] font-semibold uppercase tracking-wider text-[var(--color-text-tertiary)]">
+            Workspace
+          </div>
+        )}
+        <SidebarItem
+          icon={Users}
+          label="My Workspace"
+          to="/workspace"
+          collapsed={collapsed}
+        />
+      </div>
+
+      {/* Folders section */}
+      <div className="mt-4 flex-1 overflow-y-auto px-2">
+        {!collapsed && (
+          <button
+            onClick={() => setFoldersOpen(!foldersOpen)}
+            className="mb-1 flex w-full items-center justify-between px-3 text-[11px] font-semibold uppercase tracking-wider text-[var(--color-text-tertiary)] transition-colors hover:text-[var(--color-text-secondary)]"
+          >
+            <span>Folders</span>
+            <div className="flex items-center gap-1">
+              <Plus
+                className="h-3.5 w-3.5 opacity-0 transition-opacity group-hover:opacity-100"
+                onClick={(e) => {
+                  e.stopPropagation();
+                }}
+              />

