+          setSelectedRecipeIndex((i) => Math.min(i + 1, filteredRecipes.length - 1));
+          return;
+        }
+        if (e.key === 'ArrowUp') {
+          e.preventDefault();
+          setSelectedRecipeIndex((i) => Math.max(i - 1, 0));
+          return;
+        }
+        if (e.key === 'Enter' || e.key === 'Tab') {
+          e.preventDefault();
+          handleSelectRecipe(filteredRecipes[selectedRecipeIndex]);
+          return;
+        }
+        if (e.key === 'Escape') {
+          setInput('');
+          setSelectedRecipeIndex(0);
+          return;
+        }
