+    if (inputRef.current) {
+      inputRef.current.style.height = 'auto';
+      inputRef.current.style.height = `${Math.min(inputRef.current.scrollHeight, 160)}px`;
+    }
+  }, []);
+
+  return (
+    <div className="border-t border-[var(--color-border-primary)] bg-[var(--color-bg-primary)] px-4 py-3">
+      <div className="relative max-w-3xl mx-auto">
+        {showRecipes && (
+          <div className="absolute bottom-full left-0 right-0 mb-1 bg-[var(--color-bg-elevated)] border border-[var(--color-border-primary)] rounded-[var(--radius-lg)] shadow-[var(--shadow-lg)] overflow-hidden">
+            {filteredRecipes.map((recipe, index) => (
+              <button
+                key={recipe.id}
+                type="button"
+                onClick={() => handleSelectRecipe(recipe)}
+                className={cn(
+                  "w-full text-left px-4 py-2.5 flex items-start gap-3",
+                  "hover:bg-[var(--color-bg-hover)] transition-colors",
+                  index === selectedRecipeIndex && "bg-[var(--color-bg-hover)]"
+                )}
+              >
+                <span className="text-sm font-mono text-[var(--color-accent)]">
+                  {recipe.triggerCommand}
+                </span>
+                <div className="flex-1 min-w-0">
+                  <p className="text-sm text-[var(--color-text-primary)]">{recipe.name}</p>
+                  <p className="text-xs text-[var(--color-text-tertiary)] truncate">{recipe.description}</p>
+                </div>
+              </button>
+            ))}
+          </div>
+        )}
+        <div
+          className={cn(
+            'flex items-end gap-2',
+            'bg-[var(--color-bg-secondary)] rounded-[var(--radius-xl)]',
+            'border border-[var(--color-border-primary)]',
+            'px-4 py-2',
+            'focus-within:ring-2 focus-within:ring-[var(--color-accent)] focus-within:border-transparent',
+            'transition-all duration-150'
+          )}
+        >
+        <textarea
+          ref={inputRef}
+          value={input}
+          onChange={(e) => {
+            setInput(e.target.value);
+            setSelectedRecipeIndex(0);
+            handleInput();
+          }}
+          onKeyDown={handleKeyDown}
+          placeholder="Ask about your meetings..."
+          rows={1}
+          disabled={isProcessing}
+          className={cn(
+            'flex-1 bg-transparent text-sm text-[var(--color-text-primary)]',
+            'placeholder:text-[var(--color-text-tertiary)]',
+            'border-none outline-none resize-none',
+            'py-1 min-h-[24px] max-h-[160px]',
+            'disabled:opacity-50'
+          )}
+        />
+        <Button
+          variant="accent"
+          size="sm"
+          icon={<Send className="h-3.5 w-3.5" />}
+          onClick={handleSend}
+          disabled={!input.trim() || isProcessing}
+          loading={isProcessing}
+          className="shrink-0 mb-0.5"
+        />
+        </div>
+      </div>
+    </div>
+  );
+}
+
+// ---------------------------------------------------------------------------
+// Active Conversation View
+// ---------------------------------------------------------------------------
+
+function ActiveConversation({
+  conversation,
+  isProcessing,
+  onSend,
+}: {
+  conversation: ChatConversation;
+  isProcessing: boolean;
+  onSend: (text: string) => void;
+}) {
+  const messagesEndRef = useRef<HTMLDivElement>(null);
+
+  // Auto-scroll on new messages
+  useEffect(() => {
+    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
+  }, [conversation.messages.length, isProcessing]);
+
+  const lastAssistantIndex = useMemo(() => {
+    for (let i = conversation.messages.length - 1; i >= 0; i--) {
+      if (conversation.messages[i].role === 'assistant') return i;
+    }
+    return -1;
+  }, [conversation.messages]);
+
+  return (
+    <div className="flex-1 flex flex-col min-h-0">
+      {/* Conversation header */}
+      <div
+        className={cn(
+          'flex items-center gap-3 px-6 py-3',
+          'border-b border-[var(--color-border-primary)]',
+          'bg-[var(--color-bg-primary)]'
+        )}
+      >
+        <div
+          className={cn(
+            'flex items-center justify-center h-7 w-7 rounded-full',
+            'bg-[var(--color-accent-light)]'
+          )}
+        >
+          <Bot className="h-3.5 w-3.5 text-[var(--color-accent)]" />
+        </div>
+        <div>
+          <h2 className="text-sm font-medium text-[var(--color-text-primary)]">
+            {conversation.title}
+          </h2>
+          <p className="text-[11px] text-[var(--color-text-tertiary)]">
+            {conversation.messages.length} message{conversation.messages.length !== 1 ? 's' : ''}
+          </p>
+        </div>
+        <Badge variant="outline" size="sm" className="ml-auto">
+          {conversation.scope === 'all' ? 'All Meetings' : conversation.scope}
+        </Badge>
+      </div>
+
+      {/* Messages */}
+      <ScrollArea className="flex-1 py-6">
+        <div className="max-w-3xl mx-auto space-y-6">
+          {conversation.messages.map((msg, i) => (
