+            </div>
+          )}
+
+          <div ref={messagesEndRef} />
+        </div>
+      </ScrollArea>
+
+      {/* Input */}
+      <ChatInput onSend={onSend} isProcessing={isProcessing} />
+    </div>
+  );
+}
