+function EditableText({ value, onChange, multiline, className, placeholder }: EditableTextProps) {
+  const [isEditing, setIsEditing] = useState(false);
+  const [editValue, setEditValue] = useState(value);
+  const textareaRef = useRef<HTMLTextAreaElement>(null);
+  const inputRef = useRef<HTMLInputElement>(null);
+
+  useEffect(() => {
+    setEditValue(value);
+  }, [value]);
+
+  useEffect(() => {
+    if (isEditing) {
+      const ref = multiline ? textareaRef.current : inputRef.current;
+      if (ref) {
+        ref.focus();
+        ref.select();
+      }
+    }
+  }, [isEditing, multiline]);
+
+  const handleSave = () => {
+    onChange(editValue);
+    setIsEditing(false);
+  };
+
+  const handleKeyDown = (e: React.KeyboardEvent) => {
+    if (e.key === 'Enter' && !e.shiftKey && !multiline) {
+      e.preventDefault();
+      handleSave();
+    }
+    if (e.key === 'Escape') {
+      setEditValue(value);
+      setIsEditing(false);
+    }
+  };
+
+  if (isEditing) {
+    const sharedProps = {
+      value: editValue,
+      onChange: (e: React.ChangeEvent<HTMLTextAreaElement | HTMLInputElement>) => setEditValue(e.target.value),
+      onBlur: handleSave,
+      onKeyDown: handleKeyDown,
+      className: cn(
+        'w-full bg-[var(--color-bg-secondary)] border border-[var(--color-accent)]',
+        'rounded-[var(--radius-md)] px-3 py-2 text-sm',
+        'focus:outline-none focus:ring-2 focus:ring-[var(--color-accent)]',
+        multiline && 'min-h-[100px] resize-y',
+        className
+      ),
+      placeholder,
+    };
+
+    if (multiline) {
+      return <textarea ref={textareaRef} {...sharedProps} />;
+    }
+    return <input ref={inputRef} {...sharedProps} />;
+  }
+
+  return (
+    <div
+      onClick={() => setIsEditing(true)}
+      className={cn(
+        'cursor-text hover:bg-[var(--color-bg-hover)] rounded-[var(--radius-sm)]',
+        'px-2 py-1 -mx-2 -my-1 transition-colors',
+        !value && 'text-[var(--color-text-tertiary)] italic',
