+/*  VocabularyEditor  */
+function VocabularyEditor() {
+  const { settings, setCustomVocabulary } = useSettingsStore();
+  const [inputValue, setInputValue] = useState('');
+  const vocabulary = settings.customVocabulary ?? [];
+  
+  const addTerm = (term: string) => {
+    const trimmed = term.trim();
+    if (trimmed && !vocabulary.includes(trimmed)) {
+      setCustomVocabulary([...vocabulary, trimmed]);
+    }
+    setInputValue('');
+  };
+  
+  const removeTerm = (term: string) => {
+    setCustomVocabulary(vocabulary.filter(t => t !== term));
+  };
+  
+  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
+    if (e.key === 'Enter' || e.key === ',') {
+      e.preventDefault();
+      addTerm(inputValue);
+    }
+    if (e.key === 'Backspace' && !inputValue && vocabulary.length > 0) {
+      removeTerm(vocabulary[vocabulary.length - 1]);
+    }
+  };
+  
+  return (
+    <div className="space-y-3">
+      <div className="relative">
+        <Input
+          value={inputValue}
+          onChange={(e) => setInputValue(e.target.value)}
+          onKeyDown={handleKeyDown}
+          placeholder="Type a term and press Enter"
+          className="w-full"
+        />
+        <p className="mt-1.5 text-[11px] text-[var(--color-text-tertiary)]">
+          e.g., ACME Corp, OKRs, Q4 Roadmap
+        </p>
+      </div>
+      
+      {vocabulary.length > 0 && (
+        <div className="flex flex-wrap gap-2">
+          {vocabulary.map((term) => (
+            <span
+              key={term}
+              className={cn(
+                "inline-flex items-center gap-1.5 px-2.5 py-1",
+                "bg-[var(--color-bg-tertiary)] rounded-[var(--radius-full)]",
+                "text-sm text-[var(--color-text-primary)]"
+              )}
+            >
+              {term}
+              <button
+                onClick={() => removeTerm(term)}
+                className={cn(
+                  "p-0.5 rounded-full",
+                  "text-[var(--color-text-tertiary)] hover:text-[var(--color-text-primary)]",
+                  "hover:bg-[var(--color-bg-hover)]",
+                  "transition-colors"
+                )}
+              >
+                <X className="h-3 w-3" />
+              </button>
+            </span>
+          ))}
+        </div>
+      )}
+      
+      {vocabulary.length === 0 && (
+        <p className="text-sm text-[var(--color-text-tertiary)] py-2">
+          No custom terms added yet
+        </p>
+      )}
+    </div>
+  );
