+function ExportDropdown({ note }: { note: Note }) {
+  const [showDropdown, setShowDropdown] = useState(false);
+  const dropdownRef = useRef<HTMLDivElement>(null);
+  
+  // Close on click outside
+  useEffect(() => {
+    const handleClick = (e: MouseEvent) => {
+      if (dropdownRef.current && !dropdownRef.current.contains(e.target as Node)) {
+        setShowDropdown(false);
+      }
+    };
+    if (showDropdown) {
+      document.addEventListener('mousedown', handleClick);
+      return () => document.removeEventListener('mousedown', handleClick);
+    }
+  }, [showDropdown]);
+  
+  const generateMarkdown = (): string => {
+    const lines: string[] = [
+      `# ${note.title}`,
+      '',
+      `**Date:** ${format(new Date(note.meetingDate), 'MMMM d, yyyy')}`,
+      `**Attendees:** ${note.attendees.map(a => a.name).join(', ')}`,
+      '',
+      '## Summary',
+      note.aiSummary || '_No summary_',
+      '',
+    ];
+    
+    if (note.keyPoints.length > 0) {
+      lines.push('## Key Points');
+      note.keyPoints.forEach(kp => lines.push(`- **[${kp.type}]** ${kp.text}`));
+      lines.push('');
+    }
+    
+    if (note.actionItems.length > 0) {
+      lines.push('## Action Items');
+      note.actionItems.forEach(item => {
+        const checkbox = item.completed ? '[x]' : '[ ]';
+        const assignee = item.assignee ? ` _(${item.assignee})_` : '';
+        lines.push(`- ${checkbox} ${item.text}${assignee}`);
+      });
+      lines.push('');
+    }
+    
+    if (note.decisions.length > 0) {
+      lines.push('## Decisions');
+      note.decisions.forEach(d => lines.push(`- ${d}`));
+      lines.push('');
+    }
+    
+    if (note.userNotes) {
+      lines.push('## Notes');
+      lines.push(note.userNotes);
+    }
+    
+    return lines.join('\n');
+  };
+  
+  const generatePlainText = (): string => {
+    const lines: string[] = [
+      note.title.toUpperCase(),
+      '='.repeat(note.title.length),
+      '',
+      `Date: ${format(new Date(note.meetingDate), 'MMMM d, yyyy')}`,
+      `Attendees: ${note.attendees.map(a => a.name).join(', ')}`,
+      '',
+      'SUMMARY',
+      note.aiSummary || 'No summary',
+      '',
+    ];
+    
+    if (note.keyPoints.length > 0) {
+      lines.push('KEY POINTS');
+      note.keyPoints.forEach(kp => lines.push(` ${kp.text} [${kp.type}]`));
+      lines.push('');
+    }
+    
+    if (note.actionItems.length > 0) {
+      lines.push('ACTION ITEMS');
+      note.actionItems.forEach(item => {
+        const checkbox = item.completed ? '[x]' : '[ ]';
+        const assignee = item.assignee ? ` - ${item.assignee}` : '';
+        lines.push(`${checkbox} ${item.text}${assignee}`);
+      });
+      lines.push('');
+    }
+    
+    if (note.decisions.length > 0) {
+      lines.push('DECISIONS');
+      note.decisions.forEach(d => lines.push(` ${d}`));
+      lines.push('');
+    }
+    
+    if (note.userNotes) {
+      lines.push('NOTES');
+      lines.push(note.userNotes);
+    }
+    
+    return lines.join('\n');
+  };
+  
+  const handleCopy = async (type: 'markdown' | 'text') => {
