+
+function getBreadcrumbs(pathname: string): { label: string; path: string }[] {
+  const crumbs: { label: string; path: string }[] = [];
+
+  // Always start with Home for non-root routes
+  if (pathname !== '/') {
+    crumbs.push({ label: 'Home', path: '/' });
+  }
+
+  // Check for exact match first
+  if (routeLabels[pathname]) {
+    if (pathname !== '/') {
+      crumbs.push({ label: routeLabels[pathname], path: pathname });
+    } else {
+      crumbs.push({ label: 'Home', path: '/' });
+    }
+    return crumbs;
+  }
+
+  // Handle dynamic routes
+  const segments = pathname.split('/').filter(Boolean);
+
+  if (segments[0] === 'notes' && segments[1]) {
+    crumbs.push({ label: 'Notes', path: '/notes' });
+    crumbs.push({ label: 'Meeting Note', path: pathname });
+  } else if (segments[0] === 'meeting' && segments[1]) {
+    crumbs.push({ label: 'Active Meeting', path: pathname });
+  } else if (segments[0] === 'templates' && segments[1]) {
+    crumbs.push({ label: 'Templates', path: '/templates' });
+    crumbs.push({ label: 'Editor', path: pathname });
+  } else {
+    // Fallback: build crumbs from path segments
+    const basePath = `/${segments[0]}`;
+    const label = routeLabels[basePath] || segments[0].charAt(0).toUpperCase() + segments[0].slice(1);
+    crumbs.push({ label, path: basePath });
+    if (segments[1]) {
+      crumbs.push({ label: segments[1], path: pathname });
+    }
+  }
+
+  return crumbs;
+}
