+// ---------------------------------------------------------------------------
+// Basic markdown rendering
+// ---------------------------------------------------------------------------
+
+function MessageContent({
+  content,
+  isUser,
+}: {
+  content: string;
+  isUser: boolean;
+}) {
+  // Split by newlines and process each line
+  const lines = content.split('\n');
+
+  return (
+    <div className="space-y-1.5">
+      {lines.map((line, i) => {
+        const trimmed = line.trim();
+
+        // Empty line = paragraph break
+        if (!trimmed) return <div key={i} className="h-1" />;
+
+        // Heading (bold line starting with **)
+        if (trimmed.startsWith('**') && trimmed.endsWith('**')) {
+          return (
+            <p
+              key={i}
+              className={cn(
+                'font-semibold',
+                isUser ? 'text-white' : 'text-[var(--color-text-primary)]'
+              )}
+            >
+              {trimmed.slice(2, -2)}
+            </p>
+          );
+        }
+
+        // List items
+        if (
+          trimmed.startsWith('- ') ||
+          trimmed.startsWith('* ') ||
+          /^\d+\.\s/.test(trimmed)
+        ) {
+          const bulletMatch = trimmed.match(/^(-|\*|\d+\.)\s(.+)$/);
+          const text = bulletMatch ? bulletMatch[2] : trimmed;
+          return (
+            <div key={i} className="flex items-start gap-2 pl-1">
+              <span
+                className={cn(
+                  'mt-1.5 h-1 w-1 rounded-full shrink-0',
+                  isUser ? 'bg-white/70' : 'bg-[var(--color-text-tertiary)]'
+                )}
+              />
+              <span>
+                <InlineFormatted text={text} isUser={isUser} />
+              </span>
+            </div>
+          );
+        }
+
+        // Checkbox items
+        if (trimmed.startsWith('- [') || trimmed.startsWith('- ') || trimmed.startsWith('- ')) {
+          const isChecked = trimmed.includes('[x]') || trimmed.includes('');
+          const text = trimmed
+            .replace(/^- \[[ x]\]\s*/, '')
+            .replace(/^- []\s*/, '');
+          return (
+            <div key={i} className="flex items-start gap-2 pl-1">
+              <span
+                className={cn(
+                  'mt-0.5 text-xs shrink-0',
+                  isChecked
+                    ? isUser
+                      ? 'text-white/70'
+                      : 'text-[var(--color-success)]'
+                    : isUser
+                      ? 'text-white/50'
+                      : 'text-[var(--color-text-tertiary)]'
+                )}
+              >
+                {isChecked ? '' : ''}
+              </span>
+              <span className={isChecked ? 'line-through opacity-60' : ''}>
+                <InlineFormatted text={text} isUser={isUser} />
+              </span>
+            </div>
+          );
+        }
+
+        // Regular paragraph
+        return (
+          <p key={i}>
+            <InlineFormatted text={trimmed} isUser={isUser} />
+          </p>
+        );
+      })}
+    </div>
+  );
+}
+
+function InlineFormatted({ text, isUser }: { text: string; isUser: boolean }) {
+  // Bold text: **text**
+  const parts = text.split(/(\*\*[^*]+\*\*)/g);
+  return (
+    <>
+      {parts.map((part, i) => {
+        if (part.startsWith('**') && part.endsWith('**')) {
+          return (
+            <strong
+              key={i}
+              className={cn(
+                'font-semibold',
+                isUser ? 'text-white' : 'text-[var(--color-text-primary)]'
+              )}
+            >
+              {part.slice(2, -2)}
+            </strong>
+          );
+        }
+        return <span key={i}>{part}</span>;
+      })}
+    </>
+  );
+}
