+                className={cn(
+                  "w-full text-left px-4 py-2.5 flex items-start gap-3",
+                  "hover:bg-[var(--color-bg-hover)] transition-colors",
+                  index === selectedRecipeIndex && "bg-[var(--color-bg-hover)]"
+                )}
+              >
+                <span className="text-sm font-mono text-[var(--color-accent)]">
+                  {recipe.triggerCommand}
