+import * as React from 'react';
+import { motion, AnimatePresence } from 'framer-motion';
+import { cn } from '@/lib/cn';
+
+export interface DropdownMenuProps {
+  children: React.ReactNode;
+}
+
+export interface DropdownMenuTriggerProps {
+  children: React.ReactNode;
+  asChild?: boolean;
+}
+
+export interface DropdownMenuContentProps {
+  align?: 'start' | 'center' | 'end';
+  sideOffset?: number;
+  className?: string;
+  children?: React.ReactNode;
+}
+
+export interface DropdownMenuItemProps
+  extends React.HTMLAttributes<HTMLDivElement> {
+  disabled?: boolean;
+  destructive?: boolean;
+  icon?: React.ReactNode;
+  shortcut?: string;
+}
+
+export type DropdownMenuSeparatorProps = React.HTMLAttributes<HTMLDivElement>;
+
+export type DropdownMenuLabelProps = React.HTMLAttributes<HTMLDivElement>;
+
+interface DropdownContextValue {
+  open: boolean;
+  setOpen: (open: boolean) => void;
+  triggerRef: React.RefObject<HTMLDivElement | null>;
+}
+
+const DropdownContext = React.createContext<DropdownContextValue>({
+  open: false,
+  setOpen: () => {},
+  triggerRef: { current: null },
+});
+
+function DropdownMenu({ children }: DropdownMenuProps) {
+  const [open, setOpen] = React.useState(false);
+  const triggerRef = React.useRef<HTMLDivElement>(null);
+
+  return (
+    <DropdownContext.Provider value={{ open, setOpen, triggerRef }}>
+      <div className="relative inline-block">{children}</div>
+    </DropdownContext.Provider>
+  );
+}
+
+function DropdownMenuTrigger({ children }: DropdownMenuTriggerProps) {
+  const { open, setOpen, triggerRef } = React.useContext(DropdownContext);
+
+  return (
+    <div
+      ref={triggerRef}
+      onClick={() => setOpen(!open)}
+      className="cursor-pointer"
+    >
+      {children}
+    </div>
+  );

