+import * as React from 'react';
+import { cn } from '@/lib/cn';
+
+export interface InputProps
+  extends React.InputHTMLAttributes<HTMLInputElement> {
+  label?: string;
+  error?: string;
+  leftIcon?: React.ReactNode;
+  rightIcon?: React.ReactNode;
+  wrapperClassName?: string;
+}
+
+const Input = React.forwardRef<HTMLInputElement, InputProps>(
+  (
+    {
+      className,
+      label,
+      error,
+      leftIcon,
+      rightIcon,
+      wrapperClassName,
+      id,
+      ...props
+    },
+    ref
+  ) => {
+    const generatedId = React.useId();
+    const inputId = id || generatedId;
+
+    return (
+      <div className={cn('flex flex-col gap-1.5', wrapperClassName)}>
+        {label && (
+          <label
+            htmlFor={inputId}
+            className="text-sm font-medium text-[var(--color-text-primary)]"
+          >
+            {label}
+          </label>
+        )}
+        <div className="relative">
+          {leftIcon && (
+            <span className="absolute left-2.5 top-1/2 -translate-y-1/2 text-[var(--color-text-tertiary)] pointer-events-none">
+              {leftIcon}
+            </span>
+          )}
+          <input
+            ref={ref}
+            id={inputId}
+            className={cn(
+              'flex w-full h-8 rounded-[var(--radius-md)] px-3 text-sm',
+              'bg-[var(--color-bg-primary)] text-[var(--color-text-primary)]',
+              'border border-[var(--color-border-primary)]',
+              'placeholder:text-[var(--color-text-tertiary)]',
+              'transition-colors duration-150',
+              'focus:outline-none focus:ring-2 focus:ring-[var(--color-accent)] focus:ring-offset-0 focus:border-transparent',
+              'disabled:cursor-not-allowed disabled:opacity-50',
+              leftIcon && 'pl-9',
+              rightIcon && 'pr-9',
+              error &&
+                'border-[var(--color-error)] focus:ring-[var(--color-error)]',
+              className
+            )}
+            aria-invalid={error ? true : undefined}

