+/*  VocabularyEditor  */
+function VocabularyEditor() {
+  const { settings, setCustomVocabulary } = useSettingsStore();
+  const [inputValue, setInputValue] = useState('');
+  const vocabulary = settings.customVocabulary ?? [];
+  
+  const addTerm = (term: string) => {
+    const trimmed = term.trim();
+    if (trimmed && !vocabulary.includes(trimmed)) {
+      setCustomVocabulary([...vocabulary, trimmed]);
+    }
+    setInputValue('');
+  };
+  
+  const removeTerm = (term: string) => {
+    setCustomVocabulary(vocabulary.filter(t => t !== term));
+  };
+  
+  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
+    if (e.key === 'Enter' || e.key === ',') {
+      e.preventDefault();
+      addTerm(inputValue);
+    }
+    if (e.key === 'Backspace' && !inputValue && vocabulary.length > 0) {
+      removeTerm(vocabulary[vocabulary.length - 1]);
+    }
+  };
